    private void jLabelGravarAtrazoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabelGravarAtrazoMouseClicked
        if (JOptionPane.showInternalConfirmDialog(this, 
                "Grava o texto?", "Atenção", 
                JOptionPane.YES_NO_OPTION, 
                JOptionPane.QUESTION_MESSAGE) == JOptionPane.NO_OPTION) return;
        
        try {
            String txtAtz = jTextPaneAtrazo.getText();
            conn.SaveParameters(new String[] {"COBAUTATZ",txtAtz,"TEXTO"});
        } catch (Exception ex) {ex.printStackTrace();}
    }//GEN-LAST:event_jLabelGravarAtrazoMouseClicked

    private void jLabelGravarEmDiaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabelGravarEmDiaMouseClicked
        if (JOptionPane.showInternalConfirmDialog(this, 
                "Grava o texto?", "Atenção", 
                JOptionPane.YES_NO_OPTION, 
                JOptionPane.QUESTION_MESSAGE) == JOptionPane.NO_OPTION) return;

        try {
            String txtEmd = jTextPaneEmDia.getText();
            conn.SaveParameters(new String[] {"COBAUTEMD",txtEmd,"TEXTO"});
        } catch (Exception ex) {ex.printStackTrace();}
    }//GEN-LAST:event_jLabelGravarEmDiaMouseClicked

    private void jLabelLegendasMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jLabelLegendasMouseClicked
        JOptionPane.showMessageDialog(this, 
                "Use os comando para substituições:\n\n" +
                "[dias] - Númerode dias em atrazo.\n" +
                "[contrato] - Número do contrato do Locatário.\n" +
                "[vencimento] - Data do vencimento.\n" +
                "[nnumero] - Nosso Número da Boleta.\n" +
                "[nome] - Nome do Locatário.\n" +
                "[cpfcnpj] - Número do documento do Locatário.\n" +
                "[endereco] - Endereço do Imóvel.\n" +
                "[bairro] - Bairro do Imóvel.\n" +
                "[cidade] - Cidade do Imóvel.\n\n" +
                "[hojeextenco] - O dia do envio da menssagem dd de mmmm de aaaa.\n" +
                "[hoje] - O dia do envio da menssagem dd/mm/aaaa.", "Legendas", JOptionPane.INFORMATION_MESSAGE);
    }//GEN-LAST:event_jLabelLegendasMouseClicked

    private void LerCampos() {
        try {jTextPaneAtrazo.setText(conn.ReadParameters("COBAUTATZ"));} catch (Exception ex) {jTextPaneAtrazo.setText("");}        
        try {jTextPaneEmDia.setText(conn.ReadParameters("COBAUTEMD"));} catch (Exception ex) {jTextPaneEmDia.setText("");}        
    }

